import tippy from "tippy.js";
import "./form.css";

interface formData {
    form: string;
}

const Form = ({form}: formData) => {

    const splitForm = form.split('');

    const lastGame = splitForm.length - 1;

    const seperatedForm = splitForm.map((item: string, index: number) => {

        const isLastGame = index === lastGame;

        tippy('.tooltip', {
            content: "hello world",
            arrow: true,
            
        });

        switch (item) {
            case 'W':
                return <div key={index} style={{border: isLastGame ? "2px solid black" : "none"}} className="form-win tooltip">W</div>;
            case 'D': 
                return <div key={index} style={{border: isLastGame ? "2px solid black" : "none"}} className="form-draw tooltip">D</div>;
            case 'L': 
               return <div key={index} style={{border: isLastGame ? "2px solid black" : "none"}} className="form-loss tooltip">L</div>;
            
        }
    });

    return( 
        <div className="form-wrap">{seperatedForm}</div>
    );
}

export default Form;
