import { useEffect, useState } from "react";
import "./topLeagues.css";
import axios from "axios";
import { get } from "http";

const TopLeagues = () => {

    const [premierLeagueLogo, setPremierLeagueLogo] = useState<any>();
    const [serieALogo, setSerieALogo] = useState<any>();
    const [ligue1Logo, setLigue1Logo] = useState<any>();
    const [championsLeagueLogo, setChampionsLeagueLogo] = useState<any>();
    const [europaLeagueLogo, setEuropaLeagueLogo] = useState<any>();
    const [mlsLogo, setMlsLogo] = useState<any>();
    const [laligaLogo, setLaligaLogo] = useState<any>();
    const [bundesligaLogo, setBundesligaLogo] = useState<any>();

    useEffect(() => {

        const options = {
            method: 'GET',
            url: 'https://api-football-v1.p.rapidapi.com/v3/leagues',
            params: {season: '2024'},
            headers: {
              'x-rapidapi-key': '39078bce62msh6328de9fc911fbbp1f2c47jsn26c13a67abc9',
              'x-rapidapi-host': 'api-football-v1.p.rapidapi.com'
            }
          };

          const fetchData = async () => {

            try {
                const response = await axios.request(options);
                const leagueData = response.data.response;
                console.log(leagueData);
                
                const getLeagueByName = (countryName: string, leagueName: string) => {
                    const filteredLeagues = leagueData.filter((idxData: any) => {
                        return idxData.country.name === countryName && idxData.league.name.toLowerCase() == leagueName.toLowerCase();
                    });

                    

                    if (filteredLeagues) {

                        console.log(filteredLeagues[0].league.logo);
                    }
                }
                setPremierLeagueLogo(getLeagueByName('England', 'Premier League'));
                setBundesligaLogo(getLeagueByName('Germany', 'Bundesliga'));
                setLigue1Logo(getLeagueByName('France', 'Ligue 1'));
                setMlsLogo(getLeagueByName('USA', 'Major League Soccer'));
                setSerieALogo(getLeagueByName('Italy', 'Serie A'));
                

            } catch (error) {
                console.error(error);
            }

          }
          
          fetchData();

    }, []);




    return (
        <table className="top-leagues">
            <thead>
                <tr>
                    <td className="title">Top Leagues</td>
                </tr>
            </thead>
            <tbody>
                <tr className="league-row">
                    <td>
                        <img src="#img" className="logo" alt="logo"></img>
                    </td>
                    <td className="league-name">Test</td>
                </tr>
            </tbody>
            
        </table>
    );
}

export default TopLeagues;
