import "./arrowButton.css";
import LeftArrow from "../../icons/arrows/left-arrow.svg";
import RightArrow from "../../icons/arrows/right-arrow.svg";

interface ArrowData {
    direction: "left" | "right";
}

const ArrowButton = ({ direction }: ArrowData) => {


    return(
        // <img src={arrow} alt="arrow" className="arrow-button" />
        <div className="arrow-button">
            {direction === "left" && RightArrow}
            {direction === "right" && RightArrow}
        </div>

    );
}

export default ArrowButton;
