import React from "react";
import "./panel.css";

interface panelData {
    width: number;
    height: number;
    children?: any;
    className?: string;
}

const Panel = ({width, height, children, className}:panelData) => {


    return (
        <div className={className} style={{'--panel-width': `${width}px`, '--panel-height': `${height}px`,} as React.CSSProperties}>{children}</div>
    );
}

export default Panel;
