import { useState, useEffect } from "react";
import Scoreboard from "../../components/scoreboard/scoreboard";
import TopLeagues from "../../components/topLeagues/topLeagues";
import "./home.css";
import { ApiService } from "../../services/apiService";
import HeadlineLeague from "../../components/headlineLeague/headlineLeague";


const Home = () => {

    //   league number 2: Champ League, 39: Prem League, 78: Bundesliga, 3: Europa League, 61: Ligue 1, 140: laliga, 253: mls, 135: serie A

    let leagueInfo = [
        {leagueName: "Premier League", num: 39},
        {leagueName: "Champions League", num: 2},
        {leagueName: "Bundesliga", num: 78},
        {leagueName: "Europa League", num: 3},
        {leagueName: "Ligue 1", num: 61},
        {leagueName: "Serie A", num: 135},
        {leagueName: "MLS", num: 253},
        {leagueName: "LaLiga", num: 140},
    ];

    const leagueID = (data: number) => {
        setNum(data);
    };


    let [num, setNum] = useState(39);

    const params = new URLSearchParams();
    params.append('league', `${num}`);
    const queryString = params.toString();
    const url = `http://localhost:3000/?${queryString}`;
    const currentUrl = window.location.href;

    if (currentUrl !== url) {
        window.location.assign(url);
    } else {
        console.log('URL is already the same. No need to reload.');
    }

    // console.log(url);
    

    return (
        <div className="home">
            <div className='league'>
                <HeadlineLeague leagueNum={num} />
                <div className="league-wrap">
                    <TopLeagues leagueInfo={leagueInfo} sendLeagueID={leagueID}/>
                    <Scoreboard leagueNum={num} />
                
                </div>
            </div>
                
      </div>
    );
}

export default Home;
