        championsLeague: 'ChampionsLeague',
        laliga: 'LaLiga',
        premierLeague: 'Premier League',
        bundesliga: 'Bundesliga',
        serieA: 'Serie A',
        europaLeague: 'Europa League',
        mls: 'MLS',
        ligue1: 'Ligue 1'
    }


    useEffect(() => {

        const options = {
