import "./arrowButton.css";
import {ReactComponent as LeftArrow} from "../../icons/arrows/left-arrow.svg";
import {ReactComponent as RightArrow} from "../../icons/arrows/right-arrow.svg";

interface ArrowData {
    direction: "left" | "right";
}

const ArrowButton = ({ direction }: ArrowData) => {


    return(
        <>
            {direction === "left" && <LeftArrow className="arrow-button" />}
            {direction === "right" && <RightArrow className="arrow-button" />}    
        </>


    );
}

export default ArrowButton;
